command:
const tokens = game.canvas.tokens.controlled;
if (!tokens.length) return;

let [hp, assn] = [undefined, undefined];
for (const t of tokens) {
    const rv = await t.addWound(hp, assn);
    hp ||= rv.hp;
    assn ||= rv.assn;

    if (rv.error) {
        ui.notifications.warn(`Unable to wound <b>${t.name}</b>.`);
        console.error(rv.error);
    } else {
        ui.notifications.info(`<b>${t.name}</b> receives <b>${hp} HP</b> of damage.`);
    }
}
